#!/usr/bin/env node

const {
  version
} = require('../package.json');
const path = require('./path');
const minimist = require('minimist');
const dedent = require('dedent');
const fs = require('./fs');
const logger = require('./logger');
const Comptroller = require('./comptroller');

/**
 * The cli arguments
 * @type {object}
 */
const argv = minimist(process.argv.slice(2));

/**
 * The command to run
 * @type {string}
 */
const command = argv._[0] || 'help';

/**
 * The package root dir
 * @type {string}
 */
const root = argv._[1] || '.';

/**
 * Whether or not Comtroller should prune dependencies.
 * @type {boolean}
 */
const prune = argv.prune || argv.p;
const pruneInherited = argv['prune-inherited'] || argv.i;


const logOn = argv.log || argv.logging || argv.logOn || argv.logon;
const amd = argv.amd
let commonjs = argv.commonjs || argv.cjs;
const typescript = argv.typescript || argv.ts;
const es6 = argv.es6 || argv.mjs;

// by default set commonjs to true
if (commonjs === undefined) {
  commonjs = true
}

/**
 * Whether or not Comptroller should update the root package.
 * @type {boolean}
 */
const self = argv.self || argv.s;

/**
 * A function map for all cli commands
 * @type {Map<string, function>}
 */
const cli = {
  /**
   * Prints the help message
   */
  help() {
    logger.log(dedent `
      Comptroller ${version}

      Usage:
      --------
      comp <command> [options]

      Commands:
      --------
      help                      Show this message
      update [root-directory]   Update all subpackages of package found at [root-directory]
      link [root-directory]     Create symlink in node_modules for each subpackage found at [root-directory]
      version                   Print Comptroller version

      Options:
      --------
      --prune -p                Remove unused dependencies from subpackges' package.json
      --prune-inherited -i      Remove unused inherited fields from subpackges' package.json
      --self -s                 If set, only the root package will be updated
      --log                     Turn on logging (when resolving dependencies)
      --commonjs --cjs          CommonJS (ie. node.js require) module dependencies
      --typescript --ts         TypeScript (ie. import/export) module dependencies
      --es6 --mjs               ES6 (ie. import/export) module dependencies
  `);
  },

  /**
   * Updates all subpackages of package found at [root-directory]
   */
  async update() {
    const comp = new Comptroller({
      root,
      prune,
      pruneInherited,
      logOn,
      commonjs,
      amd,
      typescript,
      es6
    });

    if (self) {
      await comp.updateSelf();
      await comp.writePackageJson();
    } else {
      await comp.updatePackages();
      await comp.writePackages();
    }
  },

  /**
   * Creates symlink in node_modules for each subpackage found at [root-directory]
   */
  async link() {
    const comp = new Comptroller({
      root,
      prune
    });
    await comp.linkPackages();
  },

  /**
   * Prints Comptroller version
   */
  version() {
    logger.log(`Comptroller ${version}`);
  },
}

;
(async function main() {
  try {
    await cli[command]();
  } catch (err) {
    if (err instanceof Error) {
      return logger.error(err.stack);
    }
    logger.error(err);
  }
})();

const path = require('./path');
const fs = require('./fs');
const glob = require('./glob');
const logger = require('./logger');
const Package = require('./package');
const Patch = require('./patch');

/**
 * This is the main Comptroller class. It serves as the entry point into all of
 * Comptroller's higher level functionality.
 */
module.exports = class Comptroller extends Package {
  /**
   * Creates a new Comptroller instance. It accepts all of the arguments in
   * {@link Package#constructor} as well as...
   * @param {string} [opts.config.packages = packages] - The directory name
   * where packages can be found.
   */
  constructor(config) {
    super(config);

    /** @type {string} */
    this._packages = config.packages || 'packages';

    /** @type {Package[]} */
    this._children = this.readChildren();
  }

  /**
   * The packages directory.
   * @type {string}
   */
  get packages() {
    return this._packages
  }

  /**
   * The child packages.
   * @type {Package[]}
   */
  get children() {
    return this._children
  }

  /**
   * Scans the {@link Comptroller#packages} directory, finds package.json files
   * and generates {@link Package}s. Note that all package.json files must be
   * in a direct subdirectory of the packages directory.
   * @return {Package[]} - The child packages.
   */
  readChildren() {
    const packageJsons = glob.sync(path.resolve(this.root, this.packages, '*', 'package.json'), {
      ignore: this.ignore,
      nodir: true,
    });
    return packageJsons.map((pkgjson) => new Package({
      root: path.dirname(pkgjson),
      config: this
    }));
  }

  /**
   * Loops through all packages in {@link Comptroller#children} and writes it's
   * package.json.
   */
  async writePackages() {
    for (let child of this.children) {
      try {
        await child.writePackageJson()
      } catch (err) {
        logger.error(err);
        return
      }
    }
  }

  /**
   * A convenience method that locates a package in {@link Comptroller#children}
   * by it's name in {@link Package#packageJson}.
   * @param {string} name - The name of the child package.
   * @return {Package | boolean} - The found package, or `false` if not found.
   */
  getChildByName(name) {
    for (let child of this.children) {
      if (child.packageJson.name == name) return child;
    }
    return false;
  }

  /**
   * Takes an array of raw patches (returned by
   * {@link Package#generateDependencyPatches} or
   * {@link Package#generateInheritPatches}) and updates them with the
   * information in the {@link Comptroller#packageJson}
   * @param {Patch[]} patches - The patches to update.
   * @return {Patch[]} - The updated patches.
   */
  updatePatches(patches) {
    const newPatches = [];
    for (let patch of patches) {
      switch (patch.type) {
        case Patch.ADD:
        case Patch.UPDATE:
          const child = this.getChildByName(patch.name);
          const source = child ? 'local' : 'remote';
          let value = child ?
            child.packageJson.version :
            this.dependencies[patch.name];

          if (!value && patch.dev) {
            value = this.devDependencies[patch.name];
          }

          newPatches.push(new Patch(patch.type, {
            ...patch,
            source,
            value,
          }));

          break;

        case Patch.REMOVE:
          newPatches.push(new Patch(patch.type, {
            ...patch,
            disabled: !this.prune,
          }));
          break;

        case Patch.INHERIT:
          const val = this.packageJson[patch.name];
          const disabled = typeof val == 'undefined' && !this.pruneInherited;
          newPatches.push(new Patch(patch.type, {
            ...patch,
            value: val,
            disabled,
          }));
          break;

        default:
          break;
      }
    }
    return newPatches;
  }

  /**
   * Logs patch operations.
   * @param {Package} child - The child the patch is being applied to.
   * @param {Patch} patch - The patch being applied.
   */
  logPatch(child, patch) {
    const childName = child.packageJson.name;
    const disabled = patch.disabled ? 'DISABLED: ' : '';
    const dev = patch.dev ? ' dev ' : ' ';

    if ((patch.type == Patch.ADD || patch.type == Patch.UPDATE) && !patch.value) {
      if (patch.name in child.devDependencies) {
        const msg = `WARNING: '${patch.name}' required by ${childName} in non-dev source (${patch.files}) was found in package.json devDependencies.`
        logger.warn(msg);
      } else {
        const msg = `WARNING: '${patch.name}' required by ${childName} (${patch.files}) not found in package.json or local packages.`
        logger.warn(msg);
      }
      return;
    }

    switch (patch.type) {
      case Patch.ADD:
        logger.log(`${disabled}Adding ${patch.source}${dev}package '${patch.name}@${patch.value}' to package '${childName}'`);
        break;

      case Patch.UPDATE:
        const depField = patch.dev ? 'devDependencies' : 'dependencies';
        const oldVersion = child[depField][patch.name];
        if (oldVersion !== patch.value) {
          logger.log(`${disabled}Updating ${patch.source}${dev}package '${patch.name}' from ${oldVersion} to ${patch.value} in package '${childName}'`);
        }
        break;

      case Patch.REMOVE:
        logger.log(`${disabled}Removing${dev}package '${patch.name}' from '${childName}'`);
        break;

      case Patch.INHERIT:
        const oldValue = JSON.stringify(child.packageJson[patch.name]);
        const newValue = JSON.stringify(patch.value);
        if (oldValue !== newValue) {
          if (oldValue) {
            if (typeof newValue === 'undefined') {
              logger.log(`${disabled}Removing field ${patch.name} from package '${childName}'`);
            }
            else {
              logger.log(`${disabled}Updating field ${patch.name} from ${oldValue} to ${newValue} in package '${childName}'`);
            }
          } else {
            logger.log(`${disabled}Adding field ${patch.name} as ${newValue} to package '${childName}'`);
          }
        }
    }
  }

  /**
   * Analyzes the dependencies and inherits of each package and applies the
   * respective patches to each package.
   */
  async updatePackages() {
    for (let child of this.children) {
      let deps = await child.analyzeSourceDependencies();
      let patches = [
        ...child.generateDependencyPatches(deps),
        ...child.generateInheritPatches(),
      ];
      patches = this.updatePatches(patches);
      for (let patch of patches) {
        this.logPatch(child, patch);
        child.applyPatch(patch);
      }
    }
  }

  /**
   * Analyzes the dependencies of the root package and applies the respective
   * patches.
   */
  async updateSelf() {
    const deps = await this.analyzeSourceDependencies();
    let patches = this.generateDependencyPatches(deps)
    for (let child of this.children) {
      const childDeps = await child.analyzeSourceDependencies();
      const childPatches = child.generateDependencyPatches(childDeps);
      patches.push(...childPatches);
    }

    patches = this.updatePatches(patches);

    // shake out patches
    const shaken = {};
    for (let patch of patches) {
      if (patch.source == 'local') continue;

      const name = patch.name;

      if (!shaken[name]) {
        shaken[name] = patch;
        continue;
      }

      shaken[name] = Comptroller.mergePatches(shaken[name], patch);
    }

    for (let name in shaken) {
      const patch = shaken[name];
      this.logPatch(this, patch);
      this.applyPatch(patch);
    }
  }

  /**
   * Merges patches generated by child packages in order to apply to the parent
   * package. This resolves conflicts that occur when applying patches
   * generated by multiple sources.
   * @param {Patch} a - A patch
   * @param {Patch} b - Another patch
   * @return {Patch} - The merged patch
   */
  static mergePatches(a, b) {
    const priority = [Patch.UPDATE, Patch.ADD, Patch.REMOVE];
    const name = a.name;
    const value = a.value || b.value;
    const source = a.source || b.source;
    const dev = a.dev && b.dev;
    const files = [...a.files, ...b.files];
    const type = priority[
      Math.min(priority.indexOf(a.type), priority.indexOf(b.type))
    ];
    const disabled = (a.disabled || b.disabled) && type == Patch.REMOVE;
    return new Patch(type, {
      name,
      value,
      source,
      dev,
      disabled,
      files,
    });
  }

  /**
   * Links the packages to node_modules in a way that enables them to be
   * resolved by other packages by name.
   */
  async linkPackages() {
    const node_modules = path.resolve(this.root, 'node_modules');
    await fs.ensureDirPlease(node_modules);

    for (let child of this.children) {
      const name = child.packageJson.name;
      await fs.ensureSymlinkPlease(child.root, path.resolve(node_modules, name));
    }

  }
}

const fs = require('fs-extra');
const {promisify} = require('util');

for (let key of ['writeFile', 'readFile', 'readJson', 'ensureDir', 'ensureSymlink', 'remove']) {
  fs[`${key}Please`] = promisify(fs[key]);
}

module.exports = fs;

const {
  promisify
} = require('util');
const fastGlob = require('fast-glob');
const glob = require('glob');

glob.please = fastGlob // promisify(glob);

module.exports = glob;

const path = require('./path');
const glob = require('./glob');
const minimatch = require('minimatch');
const fs = require('./fs');
const builtins = require('builtin-modules');

const nodeDetective = require('detective')

const detective = {
  commonjs: nodeDetective,
  amd: require('detective-amd'),
  es6: require('detective-es6'),
  typescript: require('detective-typescript')
}

const sortPackageJson = require('sort-package-json');
const beautify = require('json-beautify');
const Patch = require('./patch');

/**
 * This is the package class. It serves as encapsulation for individual
 * packages, as a means of querying for package info, and as an outlet for
 * changes to a package.
 */
module.exports = class Package {
  /**
   * Creates a new package instance. It can accept it's configuration either
   * through the package.json file or through the constructor arguments.
   * @param {object} opts - The package options.
   * @param {string} opts.root - The root directory of the package.
   * @param {object} [opts.packageJson = fs.readJson('root package.json')] - The
   * package's package.json. If not specified, it's read from the root
   * directory.
   * @param {object} [opts.config = opts.packageJson.comptroller || {}] - The
   * Comptroller configurations options.
   * @param {glob} [opts.config.source = '⚹⚹/⚹.js'] - A glob that selects
   * the packages source files.
   * @param {glob} [opts.config.dev = '⚹⚹/test/⚹⚹/⚹.js'] - A glob that selects
   * the packages that should also include devDependencies
   * @param {glob[]} [opts.config.ignore = ['⚹⚹/node_modules/⚹⚹']] - An array
   * of globs to pass into {@link glob}'s `ignore` option when searching for
   * source files.
   * @param {Array<string|object>} [opts.config.exclude = builtins] - An array of package
   * names to ignore in all operations. Defaults to {@link builtin-modules}.
   * @param {string[]} [opts.config.inherits = []] - An array of field names to
   * inherit from the parent package.json. This is useful for values that
   * remain the same across all packages.
   * @param {object} [opts.config.detective = {}] - The options to pass through
   * to <a href="https://npmjs.com/package/@zdychacek/detective">detective</a>.
   * @param {boolean} [opts.config.prune = false] - Whether or not unused
   * dependencies should be pruned from the package.json.
   * @param {boolean} [opts.config.pruneInherited = false] - Whether or not to
   * prune inherited fields that aren't present in the root package.json
   * @param {boolean|number} [opts.config.pretty = false] - The "maximum fixed
   * character width" parameter to pass to json-beautify.
   * @param {boolean} [opts.config.commonjs = true] - search for commonjs dependencies
   * @param {boolean} [opts.config.es6 = false] - search for es6 module dependencies
   * @param {boolean} [opts.config.amd = false] - search for amd module dependencies
   * @param {boolean} [opts.config.typescript = false] - search for typescript module dependencies
   * @param {boolean} [opts.config.log = false] - Whether logging is turned on
   **/
  constructor({
    root,
    packageJson = fs.readJsonSync(path.resolve(root, 'package.json')),
    config = {},
    _config = packageJson.comptroller || {},
    source = _config.source || config.source,
    dev = _config.dev || config.dev,
    ignore = _config.ignore || config.ignore || ['**/node_modules/**'],
    exclude = _config.exclude || config.exclude || builtins,
    inherits = _config.inherits || config.inherits || [],
    detective = _config.detective || config.detective || {},
    prune = !!(_config.prune || config.prune),
    pruneInherited = !!(_config.pruneInherited || config.pruneInherited),
    pretty = _config.pretty || config.pretty,

    commonjs = !!(_config.commonjs || config.commonjs || process.env.CJS),
    typescript = !!(_config.typescript || config.typescript || process.env.TS),
    es6 = !!(_config.es6 || config.es6 || process.env.ES6),
    amd = !!(_config.amd || config.amd || process.env.AMD),
    logOn = !!(_config.logOn || config.logOn || process.env.LOG),
    modules = _config.modules || config.modules || {},
  }) {
    const moduleTypes = Object.assign(modules, {
      commonjs,
      amd,
      es6,
      typescript
    })

    function normalizeGlob(glob) {
      return glob.replace(/\|/g, ',')
    }

    source = normalizeGlob(source || this.defaultSourceFilesGlob(moduleTypes))
    dev = normalizeGlob(dev || this.defaultTestFilesGlob(moduleTypes))

    this._moduleTypes = moduleTypes

    /** @type {string} */
    this._root = root;

    /** @type {object} */
    this._packageJson = packageJson;

    /** @type {glob} */
    this._source = source;

    /** @type {glob} */
    this._dev = dev;

    /** @type {glob[]} */
    this._ignore = ignore;

    /** @type {string[]} */
    this._exclude = Package.generateExcludes(exclude);

    /** @type {string[]} */
    this._inherits = inherits;

    /** @type {object} */
    this._detective = detective;

    /** @type {boolean} */
    this._prune = prune;

    /** @type {boolean} */
    this._pruneInherited = pruneInherited;

    /** @type {boolean|number} */
    this._pretty = pretty;

    /** @type {boolean} */
    this._commonjs = commonjs;

    /** @type {boolean} */
    this._amd = amd;

    /** @type {boolean} */
    this._typescript = typescript;

    /** @type {boolean} */
    this._es6 = es6;

    /** @type {boolean} */
    this._logOn = logOn;

    this._tried = {}

    this.log({
      LOG: process.env.LOG,
      CJS: process.env.CJS,
      ES6: process.env.ES6,
      TS: process.env.TS,
      AMD: process.env.AMD,
      config,
      _config,
    })

    this.log({
      moduleTypes,
      source,
      dev
    })
  }

  defaultSourceFilesGlob(moduleTypes = {}) {
    if (moduleTypes.es6) return '**/*.m?jsx?'
    if (moduleTypes.typescript) return '**/*.tsx?'
    // if (moduleTypes.amd || moduleTypes.commonjs) return '**/*.js'
    return '**/*.js'
  }

  defaultTestFilesGlob(moduleTypes = {}) {
    if (moduleTypes.es6) return '**/*.test.m?jsx?'
    if (moduleTypes.typescript) return '**/*.test.tsx?'
    // if (moduleTypes.amd || moduleTypes.commonjs) return '**/*.test.js'
    return '**/*.test.js'
  }

  get moduleTypes() {
    return this._moduleTypes
  }

  get checkExtMap() {
    return {
      ts: /tsx?$/,
      js: /m?jsx?$/,
      es6: /mjsx?$/,
      default: /jsx?$/
    }
  }

  /**
   * The package's root directory.
   * @type {string}
   */
  get root() {
    return this._root
  }

  /**
   * An object representing the package's package.json.
   * @type {object}
   */
  get packageJson() {
    return this._packageJson
  }

  /**
   * An object representing the package's dependencies.
   * @type {object}
   */
  get dependencies() {
    return this.packageJson.dependencies || (this.packageJson.dependencies = {})
  }

  /**
   * An object representing the package's devDependencies.
   * @type {object}
   */
  get devDependencies() {
    return this.packageJson.devDependencies || (this.packageJson.devDependencies = {})
  }

  /**
   * A glob that matches the package's source files
   * @type {glob}
   */
  get source() {
    return this._source
  }

  /**
   * A glob that matches the package's dev files
   * @type {minimatch}
   */
  get dev() {
    return this._dev
  }

  /**
   * An array of globs that match files not to be included with the package's
   * source files.
   * @type {glob[]}
   */
  get ignore() {
    return this._ignore
  }

  /**
   * An array of package names to ignore in all operations.
   * @type {string[]}
   */
  get exclude() {
    return this._exclude
  }

  /**
   * An array of field names the package should inherit from it's parent
   * package.json.
   * @type {string[]}
   */
  get inherits() {
    return this._inherits
  }

  /**
   * The options to pass through to <a href="https://npmjs.com/package/@zdychacek/detective">detective</a>.
   * @type {object}
   */
  get detective() {
    return this._detective
  }

  /**
   * Whether or not unused dependencies should be pruned from the package.json.
   * @type {boolean}
   */
  get prune() {
    return this._prune
  }

  /**
   * Whether or not to prune inherited fields that aren't present in the root package.json
   * @type {boolean}
   */
  get pruneInherited() {
    return this._pruneInherited
  }

  /**
   * The "maximum fixed character width" parameter to pass to json-beautify.
   * @type {boolean|number}
   */
  get pretty() {
    return this._pretty
  }

  /**
   * Whether CommonJS dependencies are to be searched
   * @type {boolean}
   */
  get commonjs() {
    return this._commonjs
  }

  /**
   * Whether AMD dependencies are to be searched
   * @type {boolean}
   */
  get amd() {
    return this._amd
  }

  /**
   * Whether TypeScript dependencies are to be searched
   * @type {boolean}
   */
  get typescript() {
    return this._typescript
  }

  /**
   * Whether ES6 module dependencies are to be searched
   * @type {boolean}
   */
  get es6() {
    return this._es6
  }

  /**
   * Whether Logging is turned on
   * @type {boolean}
   */
  get logOn() {
    return this._logOn
  }

  get tried() {
    return this._tried || {}
  }

  reset() {
    this._tried = {}
  }

  /**
   * Writes {@link Package#packageJson} to it's respective package.json file.
   */
  async writePackageJson() {
    const packageJson = sortPackageJson(this.packageJson);
    const json = beautify(packageJson, null, 2, this.pretty);
    await fs.writeFilePlease(path.resolve(this.root, 'package.json'), json);
  }

  /**
   * Takes a dependency name and resolves it to the actual dependency name,
   * stripping any subdirectories from the dependency name (retaining @org
   * style dependencies). It also excludes dependencies with relative paths (./
   * or ../ style) and any dependencies listed in {@link Package#exclude}
   * @param {string} dependency - The dependency to resolve
   * @return {string | boolean} - The resolved dependency name, or false if the
   * the dependency is excluded.
   */
  resolveDependency(dependency) {
    if (dependency.charAt(0) == '.') return false;
    if (this.exclude.indexOf(dependency) >= 0) return false;
    const split = dependency.split('/');
    return split[0].charAt(0) == '@' ? split[0] + '/' + split[1] : split[0];
  }

  detectiveOpts(name) {
    return (this.detective || {})[name] || this.detective || {}
  }

  log(msg, data) {
    if (!this.logOn) return
    console.log({
      LOGGING: this.logOn
    })
    data ? console.log(msg, data) : console.log(msg)
  }

  error(msg, data) {
    console.error(msg, data)
    throw new Error(msg)
  }

  depsFor(name, src, file) {
    if (!this[name]) {
      this.log('depsFor: skipped', name, {
        file
      })
      return []
    }
    if (this.tried[name]) {
      this.log('depsFor: already tried', name, {
        file
      })
      return
    }

    this.log('depsFor', {
      name,
      file
    })
    const ext = this.fileExtension(file)
    const baseOpts = ext === 'tsx' ? {
      filePath: file, // to auto-detect appropriate jsx setting
    } : {}

    const opts = Object.assign(baseOpts, this.detectiveOpts(name))
    const $detective = detective[name]

    if (!$detective) {
      this.error(`detective not registered: ${name}`, {
        $detective,
        detective,
        name
      })
    }
    let deps
    try {
      deps = $detective(src, opts)
      this.tried[name] = true
    } catch (err) {
      this.error(err)
    }

    this.log('depsFor: dependencies found', {
      src,
      deps
    })
    return deps
  }

  fileExtension(file) {
    return path.extname(file).slice(1)
  }

  concatDeps(...deps) {
    deps = deps.filter(dep => Array.isArray(dep))
    return [].concat(...deps)
  }

  matchExt(ext) {
    const regExp = this.checkExtMap[ext] || this.checkExtMap.default
    const match = regExp.test(ext)
    this.log('matchExt', {
      ext,
      match
    })
    return match
  }

  resolve_ts(src, ext, file) {
    return this.matchExt(ext) ? this.depsFor('typescript', src, file) : []
  }

  resolve_es6(src, ext, file) {
    return this.matchExt(ext) ? this.depsFor('es6', src, file) : []
  }

  resolve_js(src, ext, file) {
    return this.matchExt(ext) ? this.concatDeps(
      this.depsFor('commonjs', src, file),
      this.depsFor('es6', src, file),
      this.depsFor('amd', src, file)) : []
  }

  resolve_default(src, ext, file) {
    // default fallback to include all
    return this.concatDeps(
      this.depsFor('commonjs', src, file),
      this.depsFor('es6', src, file),
      this.depsFor('typescript', src, file),
      this.depsFor('amd', src, file)
    )
  }

  resolveByExt(src, ext, {
    file,
    names
  }) {
    names = names || ['js', 'es6', 'ts', 'default']
    return names.reduce((acc, name) => {
      this.log('resolveByExt', name)
      // try to resolve using each extension until one returns dependencies
      if (acc.length > 0) {
        this.log('resolveByExt: found', acc)
        return acc
      }
      const funName = `resolve_${name}`
      const fun = this[funName].bind(this)
      const result = fun(src, ext, file)
      this.log('resolveByExt', {
        file,
        funName,
        result
      })
      if (result) {
        acc = acc.concat(result)
      }
      return acc
    }, [])
  }

  /**
   * Find dependencies in source file
   * @param {*} src
   */
  findDependencies(src, file) {
    this.reset()
    const ext = this.fileExtension(file)
    return this.resolveByExt(src, ext, {
      file
    })
  }

  /**
   * Analyzes the package's source files and returns all of the invoked
   * dependencies mapped to the files invoking them.
   * @return {Map<string, object>} - A map with the dependency names as keys
   * and the dependency metadata as values.
   */
  async analyzeSourceDependencies() {
    const globPath = path.resolve(this.root, this.source)
    const globOpts = {
      ignore: this.ignore,
      nodir: true,
    }
    const files = await glob.please(globPath, globOpts);
    const deps = {};
    this.log('analyzeSourceDependencies', {
      globPath,
      globOpts,
      files
    })

    await Promise.all(files.map(async (file) => {
      const src = await fs.readFilePlease(file, 'utf8');
      this.log('findDependencies', {
        src,
        file
      })
      // const dependencies = nodeDetective(src, this.detective);
      const dependencies = this.findDependencies(src, file)
      const relFile = path.relative(this.root, file);

      this.log(relFile)

      for (let dep of dependencies) {
        if (dep = this.resolveDependency(dep)) {
          this.log('resolved dependency', {
            dep
          })
          deps[dep] = deps[dep] || {
            files: []
          };
          this.log('add dependency to map', relFile)
          deps[dep].files.push(relFile);
        }
      }
      this.log('dependency map', JSON.stringify(deps, null, 2))
    }));
    return deps;
  }

  /**
   * Compares a dependency object (as returned from {@link Package#analyzeSourceDependencies})
   * with the dependencies listed in {@link Package@packageJson} and returns an
   * array of patches.
   * @param {object} dependencies - The dependencies to generate a patch for.
   * @return {Patch[]} - The patches that will make {@link Package#packageJson} match the inputted dependencies
   */
  generateDependencyPatches(dependencies) {
    const patches = [];
    const usedDeps = {};
    // const usedDev = {};

    for (let dep in dependencies) {
      let {
        files
      } = dependencies[dep];
      let dev = true;

      for (let file of files) {
        if (!minimatch(file, this.dev)) {
          dev = false;
          break;
        }
      }

      usedDeps[dep] = true;

      if (dev && !(dep in this.devDependencies) && !(dep in this.dependencies)) {
        patches.push(new Patch(Patch.ADD, {
          name: dep,
          dev,
          files
        }));
      } else if (!dev && !(dep in this.dependencies)) {
        patches.push(new Patch(Patch.ADD, {
          name: dep,
          files
        }));
      } else {
        if (dep in this.devDependencies) {
          patches.push(new Patch(Patch.UPDATE, {
            name: dep,
            dev: true,
            files
          }));
        } else if (dep in this.dependencies) {
          patches.push(new Patch(Patch.UPDATE, {
            name: dep,
            files
          }));
        }
      }
    }

    for (let dep in this.dependencies) {
      if (!usedDeps[dep] && this.exclude.indexOf(dep) < 0) {
        patches.push(new Patch(Patch.REMOVE, {
          name: dep
        }))
      }
    }

    return patches;
  }

  /**
   * Generates the patches that will satisfy {@link Package#inherits}
   * @return {Patch[]} - The patches that will update {@link Package#packageJson} with the inherited fields.
   */
  generateInheritPatches() {
    return this.inherits.map((name) => new Patch(Patch.INHERIT, {
      name
    }));
  }

  /**
   * Applies a given patch to {@link Package#packageJson}.
   * @param {Patch} patch - The patch to apply,
   */
  applyPatch(patch) {
    if (patch.disabled) return;

    let depField = patch.dev ? 'devDependencies' : 'dependencies';

    switch (patch.type) {
      case Patch.ADD:
      case Patch.UPDATE:
        if (typeof patch.value !== 'undefined') {
          this[depField][patch.name] = patch.value;
        }
        break;
      case Patch.REMOVE:
        delete this[depField][patch.name];
        break;
      case Patch.INHERIT:
        if (typeof patch.value !== 'undefined') {
          this.packageJson[patch.name] = patch.value;
        }
        else {
          delete this.packageJson[patch.name];
        }
      default:
        break;
    }
  }

  /**
   * Generates an exclude array
   * @param {Array<string|object>} excludes - The excludes array to use as a base.
   */
  static generateExcludes(excludes) {
    const result = [];

    for (let exclude of excludes) {
      if (typeof exclude === 'string') {
        result.push(exclude);
        continue;
      }
      if (exclude && typeof exclude === 'object') {
        if (exclude.group === 'builtins') {
          result.push(...builtins);
        }
      }
    }

    return result;
  }
}

/** @ignore */
const ADD = Symbol('ADD');
/** @ignore */
const REMOVE = Symbol('REMOVE');
/** @ignore */
const UPDATE = Symbol('UPDATE');
/** @ignore */
const INHERIT = Symbol('INHERIT');

/**
 * A class representing operations to apply to a package.
 */
module.exports = class Patch
{
  /**
   * Creates a new patch instance.
   * @param {Symbol} type - The type of patch this is.
   * @param {object} [config={}] - The configuration of the patch.
   * @param {string} config.name - The name of the item being patched.
   * @param {object} config.value - The value of the item being patched.
   * @param {string?} config.source - The dependency type being patched.
   * @param {boolean} [config.dev = false] - Whether or not this is a devDependency.
   * @param {boolean} [config.disabled = false] - Whether or not the patch is disabled.
   * @param {string[]} [config.files = []] - The files that necessitate the patch.
   */
  constructor (type, {
    name,
    value,
    source,
    dev = false,
    disabled = false,
    files = [],
  }={})
  {
   /**
    * The type of patch this is.
    * @type {Symbol}
    */
    this.type = type;

    /**
     * The name of the item being patched.
     * @type {string}
     */
    this.name = name;

    /**
     * The value of the item being patched.
     * @type {object}
     */
    this.value = value;

    /**
     * The dependency type being patched.
     * @type {string}
     */
    this.source = source;

    /**
     * Whether or not this is a devDependency
     * @type {boolean}
     */
    this.dev = dev;

    /**
     * Whether or not the patch is disabled
     * @type {boolean}
     */
    this.disabled = disabled;

    /**
     * The files that necessitate the patch.
     * @type {string[]}
     */
    this.files = files;
  }

  /**
  * A symbol representing the `ADD` operation.
  * @type {Symbol}
  */
  static get ADD () {return ADD}

  /**
  * A symbol representing the `REMOVE` operation.
  * @type {Symbol}
  */
  static get REMOVE () {return REMOVE}

  /**
  * A symbol representing the `UPDATE` operation.
  * @type {Symbol}
  */
  static get UPDATE () {return UPDATE}

  /**
  * A symbol representing the `INHERIT` operation.
  * @type {Symbol}
  */
  static get INHERIT () {return INHERIT}
}

const path = require('path');

/**
 * The new path object. Attemts to Posixify all path string responses.  Can't
 * just use path.posix because it doesn't seem to work for absolute paths.
 * @type {object}
 */
const newPath = {};

for (let key of ['resolve', 'join', 'format', 'normalize', 'relative']) {
  const origFn = path[key];
  newPath[key] = function (...args) {
    return path.posix.join(...path[key](...args).split(/\\|\/\//))
  }
}

module.exports = ({...path, ...newPath});
