/** @external {EventEmitter} https://nodejs.org/api/events.html#events_class_eventemitter */
const EventEmitter = require('events');
/** @external {fs} https://www.npmjs.com/package/fs-extra */
const fs = require('fs-extra');
/** @external {path} https://nodejs.org/api/path.html */
const path = require('path');
/** @external {promisify} https://nodejs.org/api/util.html#util_util_promisify_original */
const {promisify} = require('util');
/** @external {glob} https://www.npmjs.com/package/glob */
const glob = promisify(require('glob'));
/** @external {builtins} https://www.npmjs.com/package/builtin-modules */
const builtins = require('builtin-modules');
/** @type {Package} */
const Package = require('./package');

fs.readFilePromise = promisify(fs.readFile);
fs.writeFilePromise = promisify(fs.writeFile);
fs.ensureDirPromise = promisify(fs.ensureDir);
fs.ensureSymlinkPromise = promisify(fs.ensureSymlink);

/**
 * The core Comptroller class. It provides encapsulation for all of the stuff
 * that Comptroller is supposed to do
 */
module.exports = class Comptroller extends EventEmitter
{

  /**
   * Creates a new Comptroller instance.
   * @param {object} cfg - The configuration options
   * @param {string} [cfg.root = process.cwd()] - The project root (where your
   * main package.json lives).
   * @param {string} [cfg.packages = 'packages'] - The path from `cfg.root` to
   * the packages directory.
   * @param {object} [cfg.detective={}] - The options to pass to {@link detective}
   * @param {?string[]} [cfg.order] - The order passed to {@link format-package}
   */
  constructor ({
    root = process.cwd(),
    packages = 'packages',
    ignorePackages = builtins,
    detective = {},
    order,
  }={})
  {
    super();

    /** @type {string} */
    this._rootPath = path.resolve(root);

    /** @type {string} */
    this._packagesPath = path.resolve(root, packages);

    /** @type {object} */
    this._packages = {};

    /** @type {string[]} */
    this._ignorePackages = ignorePackages;

    /** @type {object} */
    this._detectiveOpts = detective;

    /** @type {?string[]} */
    this._packageJsonOrder = order;

    /** @type {string} */
    this._packageJsonPath = path.resolve(root, 'package.json');

    try {
      /** @type {object} */
      this._packageJson = require(this._packageJsonPath);
    }
    catch (err) {
      this._packageJson = null;
    }
  }

  /**
   * Resolves the dependencies for local packages.
   */
  async resolvePackages ()
  {
    const packagePaths = await glob(path.join(this._packagesPath, '*'), {ignore: '**/node_modules/**'});
    for (let packagePath of packagePaths) {
      const pkg = new Package(this, packagePath);
      if (pkg._packageJson == null) {
        this.emit('warn', {
          type: 'packagejson',
          path: packagePath,
        });
        continue;
      }
      await pkg.evaluateDependencies(this._detectiveOpts);
      this._packages[pkg._name] = pkg;
    }
  }

  /**
   * Updates the dependencies for local packages.
   */
  async updatePackageJson ()
  {
    for (let pkgName in this._packages) {
      this._packages[pkgName].updatePackageJson();
    }
  }

  /**
   * Updates the dependencies for local packages.
   */
  async updateDependencies ()
  {
    for (let pkgName in this._packages) {
      this._packages[pkgName].updateDependencies();
    }
  }

  /**
   * Prunes the dependencies for local packages.
   */
  async pruneDependencies ()
  {
    for (let pkgName in this._packages) {
      this._packages[pkgName].pruneDependencies();
    }
  }

  /**
   * Writes the updated package.json's for each package.
   */
  async writePackageJsons ()
  {
    for (let pkgName in this._packages) {
      const pkg = this._packages[pkgName];
      await pkg.writePackageJson();
    }
  }

  /**
   * Updates the packge.json's for all packages.
   */
  async update ()
  {
    if (this._packageJson == null) {
      console.error(`ERROR: package.json not found in ${this._rootPath}`);
      process.exit(1);
    }
    this.on('info', (info) => {
      if (info.action == 'add') {
        console.log(`Added ${info.type} package "${info.name}@${info.version}" to ${info.packageJson}`);
      }
      else if (info.action == 'update') {
        console.log(`Updated ${info.type} package "${info.name}" to version ${info.version} in ${info.packageJson}`);
      }
      else if (info.action == 'update-field') {
        console.log(`Updated field "${info.key}" to "${info.value}" in ${info.packageJson}`);
      }
    });
    this.on('warn', (warn) => {
      if (warn.type == 'missing') {
        console.warn(`WARNING: remote package "${warn.name}" invoked by ${warn.file} not found in package.json`);
      }
      else if (warn.type == 'packagejson') {
        console.log(`WARNING: package.json not found in ${warn.path}`);
      }
      else if (warn.type == 'update-field-missing') {
        console.log(`WARNING: field "${warn.key}" invoked by ${warn.packageJson} not found in main package.json`);
      }
    });
    this.on('error', (error) => {
      if (error.type == 'parse') {
        console.error(`ERROR: ${error.err.message} ${error.file}:${error.line}:${error.column}`);
      }
      else if (error.type == 'config') {
        console.error(`ERROR: ${error.err.message} in config option "${error.config}"`);
      }
      process.exit(1);
    });
    await this.resolvePackages();
    await this.updatePackageJson();
    await this.updateDependencies();
    await this.writePackageJsons();
  }

  /**
   * Prunes the packge.json's for all packages.
   */
  async prune ()
  {
    if (this._packageJson == null) {
      console.error(`ERROR: package.json not found in ${this._rootPath}`);
      process.exit(1);
    }
    this.on('info', (info) => {
      if (info.action == 'remove') {
        console.log(`Removed package "${info.name}@${info.version}" from ${info.packageJson}`);
      }
    });
    this.on('error', (error) => {
      if (error.type == 'parse') {
        console.error(`ERROR: ${error.err.message} ${error.file}:${error.line}:${error.column}`);
      }
      else if (error.type == 'config') {
        console.error(`ERROR: ${error.err.message} in config option "${error.config}"`);
      }
      process.exit(1);
    });
    await this.resolvePackages();
    await this.pruneDependencies();
    await this.writePackageJsons();
  }

  /**
   * Creates a symlink'd node_modules to local packages.
   */
  async link ()
  {
    this.on('error', (error) => {
      if (error.type == 'parse') {
        console.error(`ERROR: ${error.err.message} ${error.file}:${error.line}:${error.column}`);
      }
      else if (error.type == 'config') {
        console.error(`ERROR: ${error.err.message} in config option "${error.config}"`);
      }
      process.exit(1);
    });

    await fs.ensureDirPromise(path.join(this._packagesPath, 'node_modules'));
    await this.resolvePackages();

    for (let pkgName in this._packages) {
      const pkg = this._packages[pkgName];
      let dstpath = path.join(this._packagesPath, 'node_modules', pkgName);
      await fs.ensureSymlinkPromise(pkg._dir, dstpath);
    }
  }
}

/** @type {fs} */
const fs = require('fs-extra');
/** @type {path} */
const path = require('path');
/** @type {promisify} */
const {promisify} = require('util');
/** @type {glob} */
const glob = promisify(require('glob'));
/** @external {detective} https://www.npmjs.com/package/@zdychacek/detective */
const detective = require('@zdychacek/detective');
/** @external {format-package} https://www.npmjs.com/package/format-package */
const format = require('format-package');

/**
 * Provides encapsulation for each local package.
 */
module.exports = class Package
{
  /**
   * Creates a new package.
   * @param {Comptroller} comptroller - The parent Comptroller instance.
   * @param {string} dir - The package's directory.
   */
  constructor (comptroller, dir)
  {
    /** @type {Comptroller} */
    this._comptroller = comptroller;

    /** @type {string} */
    this._dir = dir;

    /** @type {string} */
    this._packageJsonPath = path.join(dir, 'package.json');

    try {
      /** @type {object} */
      this._packageJson = require(this._packageJsonPath);

      /** @type {string} */
      this._name = this._packageJson.name;

      /** @type {string} */
      this._version = this._packageJson.version;

      /** @type {object} */
      this._options = this._packageJson.comptroller || {};
    }
    catch (err)
    {
      this._packageJson = null;
      this._name = null;
      this._version = null;
    }

    /** @type {Map<string, string>} */
    this._dependencies = {};
  }

  /**
   * Evaluates a package's dependencies
   */
  async evaluateDependencies (detectiveOpts)
  {
    return new Promise(async (res, rej) => {
      const sourcePaths = await glob(path.join(this._dir, '**/*.js'));
      const dependencies = {};
      for (let sourcePath of sourcePaths) {
        const sourceFile = await fs.readFilePromise(sourcePath);
        let reqs;
        try {
          reqs = detective(sourceFile, detectiveOpts);
        }
        catch (err) {
          // Parser error
          if (err.loc) {
            let {line, column} = err.loc;
            return this._comptroller.emit('error', {
              type: 'parse',
              err,
              file: sourcePath,
              line,
              column,
            });
          }
          // Config error
          return this._comptroller.emit('error', {
            type: 'config',
            config: 'detective',
            err,
          });
        }
        for (let req of reqs) {
          // It's a relative dep, so skip it
          if (/^\.\//.test(req)) continue;

          const split = req.split('/');
          let name = split.shift();
          if (name.charAt(0) == '@') name += '/' + split.shift();

          dependencies[name] = {file: sourcePath, name};
        }
      }

      this._dependencies = dependencies;
      res(dependencies);
    });
  }

  /**
   * Updates a packages package.json with values found in the main package.json
   */
  async updatePackageJson ()
  {
    const {inherit} = this._options;
    const packageJson = this._packageJson;
    const comptroller = this._comptroller;
    const mainPackageJson = comptroller._packageJson;
    if (inherit) {
      for (let key of inherit) {
        if (key in mainPackageJson) {
          if (packageJson[key] == mainPackageJson[key]) continue;
          packageJson[key] = mainPackageJson[key];
          comptroller.emit('info', {
            action: 'update-field',
            key,
            value: mainPackageJson[key],
            packageJson: this._packageJsonPath,
          });
        }
        else {
          comptroller.emit('warn', {
            type: 'update-field-missing',
            key,
            packageJson: this._packageJsonPath,
          });
        }
      }
    }
  }

  /**
   * Updates a packages package.json dependencies.
   */
  async updateDependencies ()
  {
    const comptroller = this._comptroller;
    for (let depName in this._dependencies) {
      const dep = this._dependencies[depName];
      this._packageJson.dependencies = this._packageJson.dependencies || {};
      // ignore package
      if (comptroller._ignorePackages.indexOf(depName) >= 0) continue;

      // local package
      if (depName in comptroller._packages) {
        const localPackage = comptroller._packages[depName];
        if (!(depName in this._packageJson.dependencies)) {
          this._packageJson.dependencies[depName] = localPackage._version;
          comptroller.emit('info', {
            action: 'add',
            type: 'local',
            file: dep.file,
            name: depName,
            version: localPackage._version,
            packageJson: this._packageJsonPath,
          });
        }
        else if (this._packageJson.dependencies[depName] !== localPackage._version) {
          this._packageJson.dependencies[depName] = localPackage._version;
          comptroller.emit('info', {
            action: 'update',
            type: 'local',
            file: dep.file,
            name: depName,
            version: localPackage._version,
            packageJson: this._packageJsonPath,
          })
        }
      }

      // remote package
      else {
        const version = comptroller._packageJson.dependencies[depName];
        if (!version) {
          comptroller.emit('warn', {
            type: 'missing',
            file: dep.file,
            name: depName
          });
        }
        else if (!(depName in this._packageJson.dependencies)) {
          this._packageJson.dependencies[depName] = version;
          comptroller.emit('info', {
            action: 'add',
            type: 'remote',
            file: dep.file,
            name: depName,
            version,
            packageJson: this._packageJsonPath,
          });
        }
        else if (this._packageJson.dependencies[depName] != version) {
          this._packageJson.dependencies[depName] = version;
          comptroller.emit('info', {
            action: 'update',
            type: 'remote',
            file: dep.file,
            name: depName,
            version,
            packageJson: this._packageJsonPath,
          });
        }
      }
    }
  }

  /**
   * Prunes unused dependencies from a packages package.json
   */
  async pruneDependencies ()
  {
    const comptroller = this._comptroller;
    for (let depName in this._packageJson.dependencies) {
      if (!(depName in this._dependencies)) {
        const version = this._packageJson.dependencies[depName];
        delete this._packageJson.dependencies[depName];
        comptroller.emit('info', {
          action: 'remove',
          name: depName,
          version,
          packageJson: this._packageJsonPath,
        });
      }
    }
  }

  /**
   * Format's package.json using {@link format-package}
   */
  async formatPackageJson ()
  {
    const comptroller = this._comptroller;
    let opts;
    if (comptroller._packageJsonOrder) {
      opts = {order: comptroller._packageJsonOrder};
    }
    return await format(this._packageJson, opts);
  }

  /**
   * Writes a package's package.json
   */
  async writePackageJson ()
  {
    await fs.writeFilePromise(this._packageJsonPath, await this.formatPackageJson());
  }
}

